function rate(btn) {
    window.prompt("Maybe give a feedback", "No feedback was provided");

    const bt1 = document.getElementById("btn-rate-1")
    const bt2 = document.getElementById("btn-rate-2")
    const bt3 = document.getElementById("btn-rate-3")

    bt1.disabled = true;
    bt2.disabled = true;
    bt3.disabled = true;

    bt1.style.cursor = "not-allowed";
    bt2.style.cursor = "not-allowed";
    bt3.style.cursor = "not-allowed";

    if (btn == "bt1") {
        bt2.className = "key";
        bt3.className = "key";
    } else if (btn == "bt2") {
        bt1.className = "key";
        bt3.className = "key";
    } else {
        bt1.className = "key";
        bt2.className = "key";
    }
}